import { createStore } from 'vuex'
import axios from 'axios';

// json-server --watch db.json => enter this command to run the API
const URL = "http://localhost:3000/products";

export default createStore({
state: {
    products: [],
    cart: [],
},
mutations: {
    setProducts(state, payload) {
        state.products = payload;
    },
    setCart(state, payload) {
        // let isProductExists = false;
        // if (state.cart.length !== 0) {
        //     state.cart.forEach(item => {
        //         if (item.name === payload.name) isProductExists = true;
        //         isProductExists ? item.quantity++ : state.cart.push(payload);
        //         console.log(item.quantity)
        //     })
        // }
        // else state.cart.push(payload);
        // console.log('state.cart', state.cart)
        // console.log('payload', payload.quantity)
        state.cart.push(payload);
    },
    deleteProduct(state, payload) {
        state.cart.splice(payload, 1);
    }
},
actions: {
    async getProducts({commit}) {
        try {
            const products = await axios.get(URL);
            commit('setProducts', products.data);
        }
        catch {
            console.error('Couldn\'t connect to API!'); 
        }
    },
    pushToCart({commit}, payload) {
        commit('setCart', payload);
    },
    deleteProductFromCart({commit}, payload) {
        commit('deleteProduct', payload);
    }
},
getters: {

}
})
